import React, { useState } from 'react';
import './App.css';

function App() {
  // Estados para controlar o nome do Pokémon, dados, carregamento e erros
  const [pokemonName, setPokemonName] = useState('');
  const [pokemonData, setPokemonData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Função para lidar com a mudança no input
  const handleInputChange = (event) => {
    setPokemonName(event.target.value);
  };

  // Função para fazer a requisição à API
  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!pokemonName.trim()) {
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`http://localhost:8000/?pokemon_name=${pokemonName}`);
      const data = await response.json();

      if (response.ok) {
        setPokemonData(data);
      } else {
        setError('Pokémon não encontrado');
      }
    } catch (err) {
      setError('Erro ao acessar a API');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>Encontre um Pokémon</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={pokemonName}
          onChange={handleInputChange}
          placeholder="Digite o nome do Pokémon"
        />
        <button type="submit">Buscar</button>
      </form>

      {loading && <p>Carregando...</p>}

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {pokemonData && (
        <div className="pokemon-info">
          <h2>{pokemonData.name.charAt(0).toUpperCase() + pokemonData.name.slice(1)}</h2>

          {/* Display rolável das imagens */}
          <div className="image-gallery">
            {pokemonData.sprites.map((sprite, index) => (
              <img key={index} src={sprite} alt={`Pokemon sprite ${index}`} width="150" />
            ))}
          </div>

          {/* Informações gerais */}
          <div>
            <table>
              <thead>
                <tr>
                  <th>Informação</th>
                  <th>Valor</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>Altura</td>
                  <td>{pokemonData.height} decímetros</td>
                </tr>
                <tr>
                  <td>Peso</td>
                  <td>{pokemonData.weight} hectogramas</td>
                </tr>
              </tbody>
            </table>
          </div>

          {/* Tipos */}
          <div>
            <h3>Tipos:</h3>
            <ul>
              {pokemonData.types.map((type, index) => (
                <li key={index}>{type}</li>
              ))}
            </ul>
          </div>

          {/* Habilidades */}
          <div>
            <h3>Habilidades:</h3>
            <ul>
              {pokemonData.abilities.map((ability, index) => (
                <li key={index}>{ability}</li>
              ))}
            </ul>
          </div>

          {/* Estatísticas */}
          <div>
            <h3>Estatísticas:</h3>
            <table>
              <tbody>
                <tr>
                  <td>Ataque</td>
                  <td>{pokemonData.stats.attack}</td>
                </tr>
                <tr>
                  <td>Defesa</td>
                  <td>{pokemonData.stats.defense}</td>
                </tr>
                <tr>
                  <td>HP</td>
                  <td>{pokemonData.stats.hp}</td>
                </tr>
                <tr>
                  <td>Ataque Especial</td>
                  <td>{pokemonData.stats['special-attack']}</td>
                </tr>
                <tr>
                  <td>Defesa Especial</td>
                  <td>{pokemonData.stats['special-defense']}</td>
                </tr>
                <tr>
                  <td>Velocidade</td>
                  <td>{pokemonData.stats.speed}</td>
                </tr>
              </tbody>
            </table>
          </div>

          {/* Áudio do grito */}
          <div>
            <audio controls>
              <source src={pokemonData.cries} type="audio/ogg" />
              Seu navegador não suporta o áudio.
            </audio>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
