import React, { useState } from 'react';
import './App.css';

function App() {
  // Estados para controlar o nome do Pokémon, dados, carregamento e erros
  const [pokemonName, setPokemonName] = useState('');
  const [pokemonData, setPokemonData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Função para lidar com a mudança no input
  const handleInputChange = (event) => {
    setPokemonName(event.target.value);
  };

  // Função para fazer a requisição à API
  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!pokemonName.trim()) {
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`http://localhost:5000/?pokemon_name=${pokemonName}`);
      const data = await response.json();

      if (response.ok) {
        setPokemonData(data);
      } else {
        setError('Pokémon não encontrado');
      }
    } catch (err) {
      setError('Erro ao acessar a API');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>Encontre um Pokémon</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={pokemonName}
          onChange={handleInputChange}
          placeholder="Digite o nome do Pokémon"
        />
        <button type="submit">Buscar</button>
      </form>

      {loading && <p>Carregando...</p>}

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {pokemonData && (
        <div className="pokemon-info">
          <h2>{pokemonData.name.charAt(0).toUpperCase() + pokemonData.name.slice(1)}</h2>

          {/* Imagem do Pokémon */}
          <img src={pokemonData.sprites[2]} alt={pokemonData.name} width="200" />

          {/* Tipo(s) do Pokémon */}
          <div>
            <h3>Tipo(s):</h3>
            <ul>
              {pokemonData.types.map((type, index) => (
                <li key={index}>{type}</li>
              ))}
            </ul>
          </div>

          {/* Habilidades do Pokémon */}
          <div>
            <h3>Habilidades:</h3>
            <ul>
              {pokemonData.abilities.map((ability, index) => (
                <li key={index}>{ability}</li>
              ))}
            </ul>
          </div>

          {/* Estatísticas do Pokémon */}
          <div>
            <h3>Estatísticas:</h3>
            <ul>
              <li>Ataque: {pokemonData.stats.attack}</li>
              <li>Defesa: {pokemonData.stats.defense}</li>
              <li>HP: {pokemonData.stats.hp}</li>
              <li>Ataque Especial: {pokemonData.stats['special-attack']}</li>
              <li>Defesa Especial: {pokemonData.stats['special-defense']}</li>
              <li>Velocidade: {pokemonData.stats.speed}</li>
            </ul>
          </div>

          {/* Outras informações */}
          <div>
            <p><strong>Altura:</strong> {pokemonData.height} decímetros</p>
            <p><strong>Peso:</strong> {pokemonData.weight} hectogramas</p>
            <audio controls>
              <source src={pokemonData.cries} type="audio/ogg" />
              Seu navegador não suporta o áudio.
            </audio>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
