import React, { useState } from 'react';
import './App.css';

function App() {
  // Estado para armazenar o nome do Pokémon
  const [pokemonName, setPokemonName] = useState('');
  // Estado para armazenar a resposta da API (dados do Pokémon)
  const [pokemonData, setPokemonData] = useState(null);
  // Estado para controlar o carregamento
  const [loading, setLoading] = useState(false);
  // Estado para armazenar possíveis erros
  const [error, setError] = useState(null);

  // Função para lidar com a alteração no input
  const handleInputChange = (event) => {
    setPokemonName(event.target.value);
  };

  // Função para fazer a requisição à API
  const handleSubmit = async (event) => {
    event.preventDefault();

    // Verificar se o nome do Pokémon foi inserido
    if (!pokemonName.trim()) {
      return;
    }

    setLoading(true); // Começar o carregamento
    setError(null); // Limpar erros anteriores

    try {
      const response = await fetch(`http://localhost:5000/?pokemon_name=${pokemonName}`);
      const data = await response.json();

      console.log(data)

      if (response.ok) {
        setPokemonData(data); // Armazenar a resposta da API no estado
      } else {
        setError('Pokémon não encontrado'); // Caso a resposta da API seja um erro
      }
    } catch (err) {
      setError('Erro ao acessar a API'); // Caso haja erro na requisição
    } finally {
      setLoading(false); // Finalizar o carregamento
    }
  };

  return (
    <div className="App">
      <h1>Encontre um Pokémon</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={pokemonName}
          onChange={handleInputChange}
          placeholder="Digite o nome do Pokémon"
        />
        <button type="submit">Buscar</button>
      </form>

      {loading && <p>Carregando...</p>} {/* Exibir mensagem de carregamento */}

      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Exibir mensagem de erro */}

      {pokemonData && (
        <div>
          <h2>{pokemonData.name}</h2> {/* Exibir o nome do Pokémon */}
          <img src={pokemonData.image} alt={pokemonData.name} width="200" /> {/* Exibir a imagem do Pokémon */}
          {/* Adicione outros dados conforme necessário */}
        </div>
      )}
    </div>
  );
}

export default App;
