import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [pokemonName, setPokemonName] = useState('');
  const [pokemonData, setPokemonData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Função para lidar com a mudança no input
  const handleInputChange = (event) => {
    setPokemonName(event.target.value);
  };

  // Função para fazer a requisição à API
  const fetchPokemonData = async (pokemon) => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`http://localhost:8000/?pokemon_name=${pokemon}`);
      const data = await response.json();

      if (response.ok) {
        setPokemonData(data);
      } else {
        setError('Pokémon não encontrado');
      }
    } catch (err) {
      setError('Erro ao acessar a API');
    } finally {
      setLoading(false);
    }
  };

  // Função para enviar o nome do Pokémon e recarregar a página com a URL atualizada
  const handleSubmit = (event) => {
    event.preventDefault();
    if (pokemonName.trim()) {
      // Atualiza a URL para incluir o nome do Pokémon
      window.history.pushState({}, '', `/?pokemon_name=${pokemonName}`);
      fetchPokemonData(pokemonName); // Faz a requisição à API
    }
  };

  // Efeito que será executado ao carregar a página para buscar o Pokémon na URL
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const nameFromUrl = urlParams.get('pokemon_name');
    if (nameFromUrl) {
      setPokemonName(nameFromUrl);
      fetchPokemonData(nameFromUrl);
    }
  }, []);

  return (
    <div className="App">
      <h1>Encontre um Pokémon</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={pokemonName}
          onChange={handleInputChange}
          placeholder="Digite o nome do Pokémon"
        />
        <button type="submit">Buscar</button>
      </form>

      {loading && <p>Carregando...</p>}

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {pokemonData && (
        <div className="pokemon-info">
          <h2>{pokemonData.name.charAt(0).toUpperCase() + pokemonData.name.slice(1)}</h2>

          {/* Imagem do Pokémon */}
          <img src={pokemonData.sprites[2]} alt={pokemonData.name} width="150" />

          {/* Informações gerais */}
          <div className="info-table">
            <p><strong>Altura:</strong> {pokemonData.height} decímetros</p>
            <p><strong>Peso:</strong> {pokemonData.weight} hectogramas</p>
            <p><strong>Tipo(s):</strong> {pokemonData.types.join(', ')}</p>
            <p><strong>Habilidade(s):</strong> {pokemonData.abilities.join(', ')}</p>
          </div>

          {/* Estatísticas */}
          <div className="stats-table">
            <h3>Estatísticas:</h3>
            <ul>
              <li><strong>Ataque:</strong> {pokemonData.stats.attack}</li>
              <li><strong>Defesa:</strong> {pokemonData.stats.defense}</li>
              <li><strong>HP:</strong> {pokemonData.stats.hp}</li>
              <li><strong>Ataque Especial:</strong> {pokemonData.stats['special-attack']}</li>
              <li><strong>Defesa Especial:</strong> {pokemonData.stats['special-defense']}</li>
              <li><strong>Velocidade:</strong> {pokemonData.stats.speed}</li>
            </ul>
          </div>

          {/* Áudio do grito */}
          <div>
            <audio controls>
              <source src={pokemonData.cries} type="audio/ogg" />
              Seu navegador não suporta o áudio.
            </audio>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
